      // No special action, not authenticated, not loading
      setMessage("Please log in to continue.");
    }
  }, [
    location.search,
    isAuthenticated,
    isLoading,
    navigate,
    login,
    checkAuthStatus,
  ]);

  useEffect(() => {
    if (isAuthenticated && !isLoading) {
