/**
 * Generates a simple tree-like string representing the project structure
 * based on the relative paths of scanned files.
 * This structure is intended to provide high-level context to the LLM.
 *
 * @param scannedFiles An array of ScannedFile objects.
 * @returns A string representing the project structure.
 */
function generateProjectStructureString(scannedFiles: ScannedFile[]): string {
  const structurePaths: Set<string> = new Set();
  const sortedRelativePaths = scannedFiles.map(f => f.relativePath).sort(); // Sort for consistent output

  // Add all file paths and their parent directories to the set
  for (const relPath of sortedRelativePaths) {
    let currentSegment = '';
    const parts = relPath.split(path.sep);
    for (let i = 0; i < parts.length; i++) {
      currentSegment = path.join(currentSegment, parts[i]);
      if (i === parts.length - 1) { // It's a file
        structurePaths.add(currentSegment);
      } else { // It's a directory
        structurePaths.add(currentSegment + path.sep); // Add trailing slash for directories
      }
    }
  }

  // Convert the set to a sorted array for consistent output
  const items = Array.from(structurePaths).sort();

  const structuredLines: string[] = [];
  items.forEach(item => {
    // Determine depth for indentation. Count path separators.
    // For directories (ending with sep), count parts-1. For files, count parts.
    const depth = item.split(path.sep).length - (item.endsWith(path.sep) ? 1 : 0);
    const indent = '  '.repeat(Math.max(0, depth)); // Ensure non-negative depth
    structuredLines.push(`${indent}- ${item}`);
  });

  return structuredLines.join('\n');
}


