/**
 * This function will construct the final prompt string that is sent to the LLM.
 * This combines all parts of the LLMInput into a single markdown-formatted string.
 * @param llmInput The structured input object.
 * @param projectRoot The project root (passed to ensure consistency, but `llmInput.projectRoot` is the primary source).
 * @returns A string representing the full prompt for the LLM.
 */
export function buildLLMPrompt(llmInput: LLMInput, projectRoot: string): string {
  // Use `file.relativePath` from `scannedFiles` for the prompt, as it's cleaner for LLM context.
  const formattedRelevantFiles = llmInput.relevantFiles.map(file => {
    return `// File: ${file.relativePath}\n${file.content}`;
  }).join('\n\n'); // Separate files with double newlines

  return `
# AI Code Generation Request

${llmInput.additionalInstructions}

## User Request
\`\`\`text
${llmInput.userPrompt}
\`\`\`

## Project Context
${llmInput.projectStructure}

### Relevant Files (for analysis)
\`\`\`files
${formattedRelevantFiles}
\`\`\`

## Expected Output Format
${llmInput.expectedOutputFormat}
`;
}


