  const { prompt, scanDirectories, scanFiles } = req.body; // Removed systemInstruction, conversationId as they are not used directly in this flow

  if (!prompt) {
    return res.status(400).json({ message: 'Prompt is required.' });
  }

  console.log(`[API] Received generate-text request for prompt: "${prompt.substring(0, 50)}..."`);

  try {
    let allScanPaths: string[] = [];

    // If scanDirectories or scanFiles are provided, use them
    if (scanDirectories && Array.isArray(scanDirectories) && scanDirectories.length > 0) {
        allScanPaths = allScanPaths.concat(scanDirectories);
    }
    if (scanFiles && Array.isArray(scanFiles) && scanFiles.length > 0) {
        allScanPaths = allScanPaths.concat(scanFiles);
    }

    // If no specific scan paths are provided (empty arrays after concat), default to scanning the whole project root
    if (allScanPaths.length === 0) {
        allScanPaths = ['.']; 
    }

    console.log(`[API] Scanning project with paths: ${allScanPaths.join(', ')} (relative to ${PROJECT_ROOT})`);
    const scannedFiles = await scanProject(allScanPaths, PROJECT_ROOT);
    console.log(`[API] Scanned ${scannedFiles.length} files for context.`);

    const llmInput: LLMInput = prepareContextForLLM(prompt, scannedFiles, PROJECT_ROOT);

    const llmOutput = await callLLM(llmInput, PROJECT_ROOT); // Pass projectRoot to LLM orchestrator
    res.json(llmOutput);
  } catch (error: any) {
    console.error('[API] Error generating text:', error);
    res.status(500).json({ message: 'Failed to generate text.', error: error.message });
  }
});

// Endpoint to create a file
app.post('/file/create', async (req, res) => {
  const { filePath, isDirectory, content } = req.body; // isDirectory is ignored as we only create files here

  if (!filePath || content === undefined) { // content can be empty string
    return res.status(400).json({ message: 'filePath and content are required for file creation.' });
  }

  const change: ProposedFileChange = {
    filePath: filePath, // This filePath is expected to be relative from the frontend
    action: 'add',
    newContent: content,
  };

  try {
    await applyFileChange(PROJECT_ROOT, change);
    res.status(200).json({ message: `File ${filePath} created successfully.` });
  } catch (error: any) {
    console.error('[API] Error creating file:', error);
    res.status(500).json({ message: 'Failed to create file.', error: error.message });
  }
});

// Endpoint to write (modify) a file
app.post('/file/write', async (req, res) => {
  const { filePath, content } = req.body;

  if (!filePath || content === undefined) { // content can be empty string
    return res.status(400).json({ message: 'filePath and content are required for file modification.' });
  }

  const change: ProposedFileChange = {
    filePath: filePath, // This filePath is expected to be relative from the frontend
    action: 'modify',
    newContent: content,
  };

  try {
    await applyFileChange(PROJECT_ROOT, change);
    res.status(200).json({ message: `File ${filePath} modified successfully.` });
  } catch (error: any) {
    console.error('[API] Error modifying file:', error);
    res.status(500).json({ message: 'Failed to modify file.', error: error.message });
  }
});

// Endpoint to delete a file
app.post('/file/delete', async (req, res) => {
  const { filePath } = req.body;

  if (!filePath) {
    return res.status(400).json({ message: 'filePath is required for file deletion.' });
  }

  const change: ProposedFileChange = {
    filePath: filePath, // This filePath is expected to be relative from the frontend
    action: 'delete',
  };

  try {
    await applyFileChange(PROJECT_ROOT, change);
    res.status(200).json({ message: `File ${filePath} deleted successfully.` });
  } catch (error: any) {
    console.error('[API] Error deleting file:', error);
    res.status(500).json({ message: 'Failed to delete file.', error: error.message });
  }
});

app.listen(PORT, () => {
  console.log(`AI Editor API server listening on port ${PORT} with PROJECT_ROOT: ${PROJECT_ROOT}`);
  console.log('Ensure GOOGLE_GEMINI_API_KEY is set in your .env file in the monorepo root.');
});"
