    "newContent": "// src/llm/contextPreparer.ts
import * as path from 'path';
import { ScannedFile, LLMInput } from '../types';

/**
 * Prepares the context for the LLM by structuring the user prompt,
 * relevant files, and system instructions into a single LLMInput object.
 *
 * @param userPrompt The natural language request from the user.
 * @param scannedFiles An array of all files scanned from the project.
 * @param projectRoot The absolute path to the project's root directory.
 * @returns An LLMInput object ready to be sent to the LLM.
 */
export function prepareContextForLLM(userPrompt: string, scannedFiles: ScannedFile[], projectRoot: string): LLMInput {

  // 1. Generate a simplified project structure string using relative paths
  const projectStructureLines = scannedFiles
    .map(file => file.relativePath) // Use the relativePath from ScannedFile
    .sort()
    .map(relPath => `- ${relPath}`);

  const projectStructure = `Project File Structure (relative to root):\n${projectStructureLines.join('\n')}\n`;

  // 2. Define the expected output format for the LLM
  const outputSchema = `
            Your response must be a JSON object with two top-level keys: 'summary' (string) and 'changes' (array of objects).
            The 'changes' array should contain objects, each representing a file operation:
            {
              "filePath": "path/to/file.tsx", // Path relative to the project root (e.g., "src/components/MyComponent.tsx")
              "action": "add" | "modify" | "delete",
              "newContent"?: "...", // Required for 'add'/'modify', omit for 'delete'
              "reason"?: "..." // Optional, short explanation for this specific file change
            }
            Example:
            {
              "summary": "Implemented user authentication and updated Navbar component.",
              "thoughtProcess": "Created new components for login and signup, updated navigation to include auth links, and added a basic auth context.",
              "changes": [
                {
                  "filePath": "src/auth/Login.tsx",
                  "action": "add",
                  "newContent": "import React from 'react';\\nimport { useStore } from '@nanostores/react';\\nimport { authStore } from './authStore';\\n\\nfunction Login() {\\n  const $auth = useStore(authStore);\\n  // ... login form logic\\n  return <div className='p-4'>Login Form</div>;\\n}\\nexport default Login;",
                  "reason": "New login component for user authentication."
                },
                {
                  "filePath": "src/components/Navbar.tsx",
                  "action": "modify",
                  "newContent": "import React from 'react';\\nimport { Link } from 'react-router-dom';\\nimport { useStore } from '@nanostores/react';\\nimport { authStore } from '../auth/authStore';\\n\\nfunction Navbar() {\\n  const $auth = useStore(authStore);\\n  return (\\n    <nav className='bg-blue-500 p-4 text-white flex justify-between'>\\n      <Link to='/' className='font-bold text-lg'>My App</Link>\\n      <div>\\n        {$auth.isLoggedIn ? (\\n          <button onClick={() => authStore.setKey('isLoggedIn', false)} className='ml-4'>Logout</button>\\n        ) : (\\n          <>\\n            <Link to='/login' className='ml-4'>Login</Link>\\n            <Link to='/signup' className='ml-4'>Signup</Link>\\n          </>\\n        )}\\n      </div>\\n    </nav>\\n  );\\n}\\nexport default Navbar;",
                  "reason": "Added login/logout links to Navbar based on authentication status."
                },
                {
                  "filePath": "src/old/DeprecatedComponent.ts",
                  "action": "delete",
                  "reason": "Removed unused component as part of refactor."
                }
              ]
            }
          `.replace(/^\s+/gm, '');

  // 3. Construct additional instructions for the LLM
  const additionalInstructions = `
You are an elite TypeSafe UI Architect. Your goal is to generate or modify code to fulfill the user's request, strictly adhering to modern frontend best practices.
Focus on TypeScript, React (with Hooks and nanostores where applicable), and Tailwind CSS v4.
Generate clean, idiomatic, production-ready code.
Always propose a clear, refactored, and logically separated file and folder structure.
Consider type safety paramount.
If the request involves new components, ensure they are well-structured, reusable, and include necessary imports and typings.
If modifying existing components, ensure backward compatibility where reasonable, or clearly state breaking changes.
When generating new code, ensure it aligns with the existing project's style (indentation, semicolons, etc.).
Do not make assumptions about external APIs unless specified.
If you need to add a new file, propose the full content of that new file.
If you need to modify an existing file, propose the full new content of that file.
If you need to delete a file, specify its path and action 'delete'.
`;

  return {
    userPrompt: userPrompt,
    projectRoot: projectRoot,
    projectStructure: projectStructure,
    relevantFiles: scannedFiles,
    additionalInstructions: additionalInstructions,
    expectedOutputFormat: outputSchema,
  };
}

/**
 * This function will construct the final prompt string that is sent to the LLM.
 * This combines all parts of the LLMInput into a single markdown-formatted string.
 * @param llmInput The structured input object.
 * @param projectRoot The project root (passed to ensure consistency, but \`llmInput.projectRoot\` is the primary source).
 * @returns A string representing the full prompt for the LLM.
 */
export function buildLLMPrompt(llmInput: LLMInput, projectRoot: string): string {
  // Use \`file.relativePath\` from \`scannedFiles\` for the prompt, as it's cleaner for LLM context.
  const formattedRelevantFiles = llmInput.relevantFiles.map(file => {
    return `// File: ${file.relativePath}\n${file.content}`;
  }).join('\n\n'); // Separate files with double newlines

  return `
# AI Code Generation Request\n\n${llmInput.additionalInstructions}\n\n## User Request\n\`\`\`text\n${llmInput.userPrompt}\n\`\`\`\n\n## Project Context\n${llmInput.projectStructure}\n\n### Relevant Files (for analysis)\n\`\`\`files\n${formattedRelevantFiles}\n\`\`\`\n\n## Expected Output Format\n${llmInput.expectedOutputFormat}\n`;
}"
  }
