      id,
      login,
      name: displayName,
      provider,
      email: emails?.[0]?.value,
      avatar_url: photos?.[0]?.value,

    };

    const tokenDto: GitHubTokenDto = {
      accessToken,
      refreshToken,
      scope: profile._scope || null,
      tokenType: profile._tokenType || null,
    };

    done(null, { profile: profileDto, tokens: tokenDto });
  }
}


