import { promises as fs } from 'fs';
import * as path from 'path';
import { ScannedFile } from './types'; // Import our updated type

/**
 * Defines file extensions relevant to frontend development and general code.
 */
const RELEVANT_FILE_EXTENSIONS = new Set([
  '.ts', '.tsx', '.js', '.jsx', '.json', '.md', '.yml', '.html', '.css', '.scss', '.less',
  '.cjs', '.mjs', '.toml', '.xml', // config and module types
  '.jsonc', // JSON with comments
  '.vue', '.svelte', // Frontend frameworks
  '.graphql', '.gql', // GraphQL schema/queries
  '.sql', // Database schemas
  '.py', '.rb', '.go', '.java', '.c', '.cpp', '.cs', '.php', // Backend/other languages
  '.sh', '.bash', '.zsh', // Shell scripts
  '.env', // Environment files
  '.txt', // Plain text
]);

/**
 * Directories to always exclude from scanning, regardless of depth.
 * These are matched by their exact name (basename).
 */
const EXCLUDE_DIR_NAMES = new Set([
  'node_modules', '.git', '.vscode', '.idea', 'dist', 'build', 'out', 'coverage',
  '__pycache__', 'venv', 'target', 'vendor', '.ai-editor-logs'// common for other languages
]);

/**
 * Specific file names to always exclude from scanning, regardless of depth.
 * These are matched by their exact name (basename).
 */
const EXCLUDE_FILE_NAMES = new Set([
  'package-lock.json', 'yarn.lock', 'pnpm-lock.yaml', '.DS_Store', // Lock files and system files
]);

/**
 * Specific file names that should always be included, even if their extension is not in RELEVANT_FILE_EXTENSIONS,
 * or if they are common dotfiles (e.g., config files).
 */
const RELEVANT_CONFIG_FILENAMES = new Set([
  'package.json', 'tsconfig.json', 'vite.config.ts', 'webpack.config.js',
  'rollup.config.js', 'tailwind.config.ts', '.gitignore', '.eslintrc.js',
  '.prettierrc.js', 'Dockerfile', 'Makefile', 'LICENSE', 'README.md', 'README.txt',
