]);


/**
 * Scans specified paths (which can be directories or individual files) recursively for relevant code files
 * and reads their content. Ensures file paths are absolute and also provides a path relative to the project root.
 * Handles multiple scan paths and deduplicates files.
 *
 * @param scanPaths An array of paths (relative or absolute) to scan. Each path can be a directory or a file.
 * @param projectRoot The root directory of the project, used for calculating relative paths.
 * @param verbose If true, logs detailed information during scanning.
 * @returns A promise that resolves to an array of ScannedFile objects.
 */
export async function scanProject(
  scanPaths: string[],
  projectRoot: string,
  verbose: boolean = false
): Promise<ScannedFile[]> {
  const allScannedFiles: ScannedFile[] = [];
  const processedAbsolutePaths = new Set<string>(); // To deduplicate files if multiple scan paths overlap

  // Process each path in the input array
  for (const currentPath of scanPaths) {
    const absolutePath = path.resolve(projectRoot, currentPath);

    if (processedAbsolutePaths.has(absolutePath)) {
      if (verbose) {
        console.log(`  Skipping '${absolutePath}' (already processed).`);
      }
      continue;
    }

    let stats;
    try {
      stats = await fs.stat(absolutePath);
    } catch (error) {
      console.error(`Error accessing path '${absolutePath}': ${(error as Error).message}. Skipping.`);
      continue;
    }

    if (stats.isFile()) {
      // If the path is an explicit file, include it directly
      try {
        const content = await fs.readFile(absolutePath, 'utf-8');
        const relativeToProjectRoot = path.relative(projectRoot, absolutePath);
        allScannedFiles.push({
          filePath: absolutePath,
          relativePath: relativeToProjectRoot,
          content: content,
        });
        processedAbsolutePaths.add(absolutePath);
        if (verbose) {
          console.log(`  Included explicit file: ${relativeToProjectRoot}`);
        }
      } catch (readError) {
        console.warn(`Warning: Could not read explicit file '${absolutePath}': ${(readError as Error).message}`);
      }
    } else if (stats.isDirectory()) {
      // If the path is a directory, use the recursive scanning logic
      if (verbose) {
        console.log(`  Initiating recursive scan for directory: ${absolutePath}`);
      }
      const queue: string[] = [absolutePath];

      while (queue.length > 0) {
        const currentDir = queue.shift()!;
        let entries;
        try {
          entries = await fs.readdir(currentDir, { withFileTypes: true });
        } catch (error) {
          console.error(`Error reading directory '${currentDir}': ${(error as Error).message}. Skipping.`);
          continue;
        }

        for (const entry of entries) {
          const entryFullPath = path.join(currentDir, entry.name);
          const relativeToProjectRoot = path.relative(projectRoot, entryFullPath);

          // --- Exclusion Logic for recursive directory scanning ---
          // 1. Exclude specific directory names (e.g., node_modules, .git)
          if (entry.isDirectory() && EXCLUDE_DIR_NAMES.has(entry.name)) {
            if (verbose) {
              console.log(`    Excluding directory: ${relativeToProjectRoot}`);
            }
            continue;
          }

          // 2. Exclude specific file names (e.g., package-lock.json, .DS_Store)
          if (entry.isFile() && EXCLUDE_FILE_NAMES.has(entry.name)) {
            if (verbose) {
              console.log(`    Excluding file: ${relativeToProjectRoot}`);
            }
            continue;
          }

          if (entry.isDirectory()) {
            queue.push(entryFullPath);
          } else if (entry.isFile()) {
            const ext = path.extname(entry.name).toLowerCase();

            // Determine if the file should be included based on extension or explicit filename
            const isIncluded = RELEVANT_FILE_EXTENSIONS.has(ext) || RELEVANT_CONFIG_FILENAMES.has(entry.name);

            if (isIncluded) {
              // Add file only if its absolute path hasn't been processed yet (for deduplication)
              if (!processedAbsolutePaths.has(entryFullPath)) {
                try {
                  const content = await fs.readFile(entryFullPath, 'utf-8');
                  allScannedFiles.push({
                    filePath: entryFullPath, // Absolute path
                    relativePath: relativeToProjectRoot, // Path relative to project root
                    content: content,
                  });
                  processedAbsolutePaths.add(entryFullPath); // Mark as processed
                  if (verbose) {
                    console.log(`    Included: ${relativeToProjectRoot}`);
                  }
                } catch (readError) {
                  console.warn(`Warning: Could not read file '${entryFullPath}': ${(readError as Error).message}`);
                }
              } else if (verbose) {
                console.log(`    Skipping (already processed): ${relativeToProjectRoot}`);
              }
            } else {
              if (verbose) {
                console.log(`    Skipping file: ${relativeToProjectRoot} (unsupported type)`);
              }
            }
          }
        }
      }
    } else {
      console.warn(`Warning: '${absolutePath}' is neither a file nor a directory. Skipping.`);
    }
  }

  return allScannedFiles;
}


