  .action(async (paths: string[], options: { verbose?: boolean; showContent?: boolean }) => {
    // If no paths are provided, default to current working directory
    const targetPaths = paths.length > 0 ? paths : ['.'];
    const projectRoot = process.cwd(); // For 'scan' command, assume CWD is the project root

    console.log(`Scanning paths: ${targetPaths.map(p => path.resolve(projectRoot, p)).join(', ')}`);
    if (options.verbose) {
      console.log('Verbose mode enabled.');
    }

    try {
      // Pass the array of targetPaths and the projectRoot to scanProject
      const scannedFiles: ScannedFile[] = await scanProject(targetPaths, projectRoot, options.verbose);
      console.log('\n--- Scan Complete ---');
      console.log(`Found ${scannedFiles.length} files.`);

      if (scannedFiles.length > 0) {
        console.log('Sample files found:');
        scannedFiles.slice(0, 5).forEach((file) => {
          // Display path relative to CWD for user clarity
          const displayPath = path.relative(process.cwd(), file.filePath);
          console.log(`  - ${displayPath}`);
          if (options.showContent) {
            const contentSnippet =
              file.content.substring(0, 200) + (file.content.length > 200 ? '...' : '');
            console.log(
              `    Content snippet:\n${contentSnippet
                .split('\n')
                .map((line) => `      ${line}`)
                .join('\n')}\n`,
            );
          }
