function getGit(baseDir: string): SimpleGit {
  return simpleGit(baseDir, { binary: 'git' });
}

/**
 * Generates a simple tree-like string representing the project structure
 * based on the relative paths of scanned files.
 * This structure is intended to provide high-level context to the LLM.
 *
 * @param scannedFiles An array of ScannedFile objects.
 * @returns A string representing the project structure.
 */
function generateProjectStructureString(scannedFiles: ScannedFile[]): string {
  const structurePaths: Set<string> = new Set();
  const sortedRelativePaths = scannedFiles.map(f => f.relativePath).sort(); // Sort for consistent output

  // Add all file paths and their parent directories to the set
  for (const relPath of sortedRelativePaths) {
    let currentSegment = '';
    const parts = relPath.split(path.sep);
    for (let i = 0; i < parts.length; i++) {
      currentSegment = path.join(currentSegment, parts[i]);
      if (i === parts.length - 1) { // It's a file
        structurePaths.add(currentSegment);
      } else { // It's a directory
        structurePaths.add(currentSegment + path.sep); // Add trailing slash for directories
      }
    }
  }

  // Convert the set to a sorted array for consistent output
  const items = Array.from(structurePaths).sort();

  const structuredLines: string[] = [];
  items.forEach(item => {
    // Determine depth for indentation. Count path separators.
    // For directories (ending with sep), count parts-1. For files, count parts.
    const depth = item.split(path.sep).length - (item.endsWith(path.sep) ? 1 : 0);
    const indent = '  '.repeat(Math.max(0, depth)); // Ensure non-negative depth
    structuredLines.push(`${indent}- ${item}`);
  });

  return structuredLines.join('\n');
}

// src/types.ts

/**
 * Represents a file that has been scanned from the project,
 * including its absolute path, its path relative to the project root, and its textual content.
 */
export interface ScannedFile {
  filePath: string; // Absolute path to the file
  relativePath: string; // Path relative to the project root (e.g., "src/components/MyComponent.tsx")
  content: string;
}

/**
 * Represents a proposed change to a file.
 * This is the structured output we expect from the LLM.
 */
export interface ProposedFileChange {
  filePath: string; // Absolute path to the file to be changed
  action: 'add' | 'modify' | 'delete';
  /**
   * For 'add' or 'modify' actions, this is the new content of the file.
   * For 'delete' actions, this field is not used.
   */
  newContent?: string;
  /**
   * An optional human-readable reason or summary for the change.
   */
  reason?: string;
}

/**
 * Represents the structured input that will be sent to the LLM.
 */
export interface LLMInput {
  userPrompt: string;
  projectRoot: string;
  projectStructure: string; // A high-level overview of the project directory (e.g., tree string)
  relevantFiles: ScannedFile[];
  additionalInstructions: string; // Specific behavioral instructions for the LLM
  expectedOutputFormat: string; // Instructions on the JSON format for the LLM's response
}

/**
 * Represents the structured output received from the LLM.
 */
export interface LLMOutput {
  changes: ProposedFileChange[];
  summary: string; // A concise summary of all changes made/proposed
  thoughtProcess?: string; // LLM's detailed reasoning for the changes
}

/**
 * Represents the authentication data stored by the CLI after a successful OAuth login.
 */
export interface AuthData {
  accessToken: string;
  userId: string;
  userEmail: string;
  userName?: string;
  userImage?: string;
  userRole: string; // Corresponds to Role enum on backend, but kept as string for CLI simplicity
  username?: string;
  provider?: 'google' | 'github';
}
